#nombre del archivoarchivo_datos="datos.txt"#funcion para escribirdef escribir_archivo(datos):    with open(archivo_datos, "w") as archivo:        for registro in datos:            archivo.write(",".join(registro) + "\n")#funcion para leer un archivodef leer_archivo():    if os.path.exists(archivo_datos):      with open(archivo_datos, "r") as archivo:          datos=[linea.strip().split(",") for linea in archivo.readlines()]    else:        datos=[]    return datos#ingresardef ingresar():    datos=leer_archivo()    ide=input("Ingrese la identificacion: ")    apellido=input("Ingrese el apellido: ")    nombre=input("Ingrese el nombre: ")    datos.append([ide,apellido,nombre])    escribir_archivo(datos)    print("El registro se agrego con exito")    # Menú de opcionesdef menu():    while True:        print("Menú de Opciones:")        print("1. Ingresar un nuevo registro")        print("2. Consultar los registros")        print("3. Modificar un registro")        print("4. Eliminar un registro")        print("5. Salir")        opcion = input("Seleccione una opción: ")        if opcion == "1":            ingresar()        elif opcion == "2":            consultar()        elif opcion == "3":            modificar()        elif opcion == "4":            eliminar()        elif opcion == "5":            print("Saliendo del programa...")            break        else:            print("Opción no válida, intente de nuevo.\n")# Iniciar el programaif __name__ == "__main__":    menu()